    constructor(private http: Http, private loginService: LoginService) {
    }

    get$CPluralN$() {
        return this.http.get(this.loginService.apiPath + '$CFN$', this.loginService.createHeader())
            .map(response => response.json());
    }

    get$CN$(id: number | string) {
        return this.http.get(this.loginService.apiPath + '$CFN$/' + id, this.loginService.createHeader())
            .map(response => response.json());
    }

    create$CN$(body) {
        return this.http.post(this.loginService.apiPath + '$CFN$', body, this.loginService.createHeader())
            .map(response => response.json());
    }

    update$CN$(body, id: number | string) {
        return this.http.put(this.loginService.apiPath + '$CFN$/' + id, body, this.loginService.createHeader())
            .map(response => response.json());
    }

    update$CN$Status(body, id: number | string) {
        return this.http.patch(this.loginService.apiPath + '$CFN$/' + id + '/changestatus', body, this.loginService.createHeader());
    }

