@Component({
    moduleId: module.id,
    templateUrl: '$CFN$-add-new.component.html'
})
export class $CN$AddNewComponent implements OnInit {

    submitted = false;
    active = true;
    $CFN$Model = new $CN$('');

    constructor(private route: ActivatedRoute,
                private router: Router,
                private $CFN$Service: $CN$Service,
                private location: Location) {
    }

    ngOnInit() {
        let id = parseInt(this.route.snapshot.params['id'], 10) || 0;
        if (id > 0) {
            this.get$CN$Detail(id);
        }
    }

    onSubmitNew() {
        console.log(this.$CFN$Model);
        this.submitTrue();

        if (this.$CFN$Model.id > 0) {
            this.$CFN$Service.update$CN$(this.$CFN$Model, this.$CFN$Model.id).subscribe(
                response => {
                    UIToastr.ok(response.name, 'OK');
                    this.router.navigate(['$CPluralN$/list']);
                },
                err => {
                    UIToastr.error(err);
                    this.submitFalse();
                },
                () => this.submitFalse()
            );
        } else {
            this.$CFN$Service.create$CN$(this.$CFN$Model).subscribe(
                response => {
                    UIToastr.ok(response.name, 'OK');
                    this.newForm();
                },
                err => {
                    UIToastr.error(err);
                    this.submitFalse();
                },
                () => this.submitFalse()
            );
        }
    }

    // get diagnostic() {
    //     return JSON.stringify(this.loginModel);
    // }

    ngAfterViewInit() {
        this.submitted = false;
    }

    newForm() {
        this.$CFN$Model = new $CN$('');
        // this.active = false;
        // setTimeout(()=>this.active = true, 0);
    }

    private submitTrue() {
        this.submitted = true;
        Spinner.show();

    }

    private submitFalse() {
        this.submitted = false;
        Spinner.hide();
    }

    get$CN$Detail(id: number) {
        this.active = false;
        Spinner.show();
        this.$CFN$Service.get$CN$(id).subscribe(
            response => {
                console.log(response);
                this.$CFN$Model = response;
                this.active = true;
                // setTimeout(()=>this.active = true, 0);
            },
            err => UIToastr.error(err),
            () => Spinner.hide()
        );
    }

