import {Component, OnInit} from '@angular/core';
import {appConfigs} from "../app.config";

import {
    $CN$,
    $CN$Service
} from './$CFN$.service';

declare var Spinner, jQuery, UIToastr, $: any;

@Component({
    moduleId: module.id,
    templateUrl: '$CFN$-list.component.html'
})
export class $CN$ListComponent implements OnInit {

    constructor(private $CFN$Service: $CN$Service) {
    }

    active = false;
    $CFN$s: $CN$[];

    ngOnInit() {
        this.refresh$CN$s();
    }

    ngAfterViewInit() {

    }

    refresh$CN$s() {
        Spinner.show();
        this.$CFN$Service.get$CN$s().subscribe(
            response => {
                console.log(response);
                this.$CFN$s = response;
                setTimeout(()=> this.tableInit(), 100);
            },
            err => UIToastr.error(err),
            () => Spinner.hide()
        );
    }

    tableInit() {
        this.active = true;

        var DTM = function () {
            var initTable = function () {
                var table = $('#$CFN$s_table');
                if (!$.fn.DataTable.isDataTable('#$CFN$s_table')) {
                    table.dataTable({
                        language: {
                            url: '/locale/datatable-locale.' + appConfigs.currentLocale +'.json'
                        },
                        bStateSave: false,
                        pagingType: 'bootstrap_extended',
                        lengthMenu: [
                            [20, 50, -1],
                            [20, 50, 'All']
                        ],
                        pageLength: 20,
                        columnDefs: [{
                            orderable: false,
                            targets: [0, 2]
                        }, {
                            searchable: false,
                            targets: [0, 2]
                        }],
                        order: [
                            [0, 'asc']
                        ]
                    });
                }
            }
            return {
                init: function () {
                    if (!jQuery().dataTable) {
                        return;
                    }
                    initTable();
                }
            };

        }();
        DTM.init();

    }

}$END$
